import './App.css';
import { useState } from 'react';
import { marked } from 'marked';

function App() {

  const [text, setText] = useState('')



  return (
    <div className="App">
      <textarea id='textarea' onChange={(e) => setText(e.target.value)}></textarea>
      <p className='preview' >{marked.parse(text)}</p>
    </div>
  );
}

export default App;
