import './App.css';
import React, { useState } from 'react';
import { marked } from 'marked';
import tachyons from 'tachyons'



function App() {

  const [text, setText] = useState('')

  const Answer = () => {
    const answer = marked.parse(text)
    return answer
  }



  return (
    <div className="App">
      <textarea id='textarea' onChange={(e) => setText(e.target.value)} className='inf' v-model="markdown"></textarea>
      <div
        className='preview'
        dangerouslySetInnerHTML={{ __html: Answer() }}>
        </div>
    </div>
  );
}

export default App;
