import './App.css';
import React, { useState } from 'react';
import { marked } from 'marked';


function App() {


  let words = `# Hello, There
  ## Welcome to my Page

  There's also [links](https://www.freecodecamp.org) to joining Free Code Camp and learning

  > These are a numbered list!
  1. And this is insane
  1. Its so fun!

  > This **word** is bold. This <em>word</em> is italic`
  const [text, setText] = useState(words);

  const answer = marked.parse(text)
  console.log(answer)

  return (
    <div className="App">
      <div>
        <h3 className='title'>Markdown</h3>
        <textarea
          value={text}
          id='editor'
          onChange={(e) => setText(e.target.value)} />
      </div>
      <div>
        <h3 className='bold'>Preview</h3>
        <div id="preview" 
        className='preview'> {answer}
        </div>
      </div>
    </div>
  );
}

export default App;
