import './App.css';
import React, { useState } from 'react';
import { marked } from 'marked';

function App() {

  const [text, setText] = useState('')

  const Answer = () => {
    const answer = marked.parse(text)
    React.createElement(
      answer,
      {color: 'black'}
    )
  }



  return (
    <div className="App">
      <textarea id='textarea' onChange={(e) => setText(e.target.value)} className='inf' v-model="markdown"></textarea>
      <preview className='preview' >{Answer}
      </preview>
    </div>
  );
}

export default App;
