import './App.css';
import { useState } from 'react';
import { marked } from 'marked';

function App() {

  const [text, setText] = useState('')

  const answer = marked.parse(text)
    console.log(answer)


  return (
    <div className="App">
      <textarea id='textarea' onChange={(e) => setText(e.target.value)} className='inf' v-model="markdown"></textarea>
      <preview className='preview' >{marked.parse(text)}
      </preview>
    </div>
  );
}

export default App;
